from typing import Dict, NamedTuple, Optional

from BaseClasses import Item, ItemClassification


class FFXIITMItem(Item):
    game: str = "Kingdom Hearts Chain of Memories"


class FFXIITMItemData(NamedTuple):
    category: str
    sub: str = "None"
    code: Optional[int] = None
    classification: ItemClassification = ItemClassification.filler
    max_quantity: int = 1
    weight: int = 1


def get_items_by_category(category: str, disclude: list) -> Dict[str, FFXIITMItemData]:
    item_dict: Dict[str, FFXIITMItemData] = {}
    for name, data in item_table.items():
        if data.category == category and all(x not in name for x in disclude):
            item_dict.setdefault(name, data)

    return item_dict


